import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [videoURL, setVideoURL] = useState('');
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    setResults([]);

    try {
      // Make the API call to our Node.js server
      const response = await axios.post('http://localhost:8080/api/analyze', { videoURL });
      setResults(response.data);
    } catch (err) {
      const errorMessage = err.response ? err.response.data.error : 'Failed to connect to the server.';
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>YouTube Comment Sentiment Analyzer</h1>
        <p>Paste a YouTube video URL below to analyze its comments.</p>
      </header>
      <main>
        <form onSubmit={handleSubmit} className="url-form">
          <input
            type="text"
            value={videoURL}
            onChange={(e) => setVideoURL(e.target.value)}
            placeholder="Enter YouTube Video URL"
            required
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Analyzing...' : 'Analyze'}
          </button>
        </form>

        {error && <p className="error-message">{error}</p>}

        <div className="results-container">
          {results.map((result, index) => (
            <div key={index} className="comment-card">
              <p className="comment-text">"{result.comment}"</p>
              <p className={`sentiment-text sentiment-${result.classification.toLowerCase()}`}>
                Sentiment: <strong>{result.classification}</strong>
              </p>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
